/* Variables */

:root {
  --bg-2: #222;
  --bg-3: #111;
  --bg-main: #333;
  --btn-bg: transparent;
  --btn-border: transparent;
  --btn-text: var(--highlight);
  --btn-active-bg: transparent;
  --btn-active-border: var(--btn-active-text);
  --btn-active-text: var(--highlight-active);
  --btn-primary-active-bg: var(--highlight-active);
  --btn-primary-bg: var(--highlight);
  --btn-primary-active-text: var(--highlight-text);
  --btn-primary-text: var(--highlight-text);
  --conversation-admin-bg: #222;
  --conversation-admin-link-hover: #279dd9;
  --conversation-admin-link: #279dd9;
  --conversation-admin-text: #eee;
  --conversation-customer-bg: #444;
  --conversation-customer-link-hover: #8ec4fd;
  --conversation-customer-link: #6cb3ff;
  --conversation-customer-text: #eee;
  --conversation-mention-bg: #fbc916;
  --conversation-mention-text: #222;
  --conversation-note-bg: #FEEDAF;
  --conversation-note-link-hover: #5783ea;
  --conversation-note-link: #2355ca;
  --conversation-note-text: #000;
  --highlight-active: rgb(90, 180, 250);
  --highlight-text: #111;
  --highlight: rgb(200, 217, 230);
  --hr: rgba(0,0,0,.1);
  --intercom-bubble-bg: var(--highlight);
  --intercom-bubble-text: var(--highlight-text);
  --nav-bg: #111;
  --notif-bg: #ffe2b3;
  --notif-text: #111;
  --scrollbar-bg: #555;
  --scrollbar-thumb: #222;
  --scrollbar-width: 8px;
  --start-logo: url(https://gunnyarts.github.io/ShadowCom/images/intercom-wide.png);
  --text-faded: #999;
  --text-link: var(--highlight);
  --text-link-hover: var(--highlight-active);
  --text-main: #eee;
  --tooltip-bg: var(--bg-main);
  --tooltip-link: #5ca2d6;
  --tooltip-text: var(--text-main);
}
